import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const products = [
  { id: 1, name: "Smartphone", price: "$499", image: "data:image/jpeg;base64," },
  { id: 2, name: "Laptop", price: "$899", image: "data:image/jpeg;base64," },
  { id: 3, name: "Headphones", price: "$199", image: "data:image/jpeg;base64," },
  { id: 4, name: "Smartwatch", price: "$149", image: "data:image/jpeg;base64," },
  { id: 5, name: "Camera", price: "$699", image: "data:image/jpeg;base64," },
  { id: 6, name: "Gaming Console", price: "$399", image: "data:image/jpeg;base64," },
  { id: 7, name: "Tablet", price: "$299", image: "data:image/jpeg;base64," },
  { id: 8, name: "Wireless Speaker", price: "$99", image: "data:image/jpeg;base64," },
];

const PopularProducts = () => {
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: products.length > 5 ? 5 : products.length,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="container mx-auto py-10">
      <h2 className="text-3xl font-bold text-center mb-6">Popular Products</h2>
      <Slider {...settings}>
        {products.map((product) => (
          <div key={product.id} className="p-4">
            <div className="bg-white shadow-md rounded-lg overflow-hidden">
              <img src={product.image} alt={product.name} className="w-full h-40 object-contain" />
              <div className="p-4 text-center">
                <h3 className="text-lg font-semibold">{product.name}</h3>
                <p className="text-gray-700">{product.price}</p>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default PopularProducts;
